
public class ArrangeEvenOdd {

	public static void main(String[] args) {
		// TODO Auto-generated method stub
int n[]= {3,2,2};
for(int i=0;i<n.length;i++) {
	if(n[i]%2==0) {
		System.out.print(n[i]+" ");
	}
	
}
for(int j=0;j<n.length;j++) {
	if(n[j]%2!=0) {
	System.out.print(n[j]+" ");
}

	
}
	}

}
